ner object, .embed-container embed {position: absolute;top: 0;left: 0;width: 100%;height: 100%;border: none;}@media screen and (-webkit-min-device-pixel-ratio:0) {.embed-container {-webkit-overflow-scrolling: touch;overflow-y: scroll;}}@media only screen and (-webkit-min-device-pixel-ratio: 0) and (min-width: 981px) {.embed-container {overflow: hidden;}}body {font-size:80%;color:#222;background:#fff;} @import url(https://fonts.googleapis.com/css?family=Open+Sans);* {box-sizing: border-box;}.wrapper {}header {padding: 0 15px;}.columns {display: flex;flex-flow: row wrap;justify-content: center;margin: 5px 0;}.column {flex: 1;border: 1px solid gray;margin: 2px;padding: 10px;}.column:first-child {margin-left: 0;}.column:last-child {margin-right: 0;}footer {padding: 0 15px;}@media screen and (max-width: 980px) {.columns .column {margin-bottom: 5px;flex-basis: 40%;}.columns .column:nth-last-child(2) {margin-right: 0;}.columns .column:last-child {flex-basis: 100%;margin: 0;}}@media screen and (max-width: 680px) {.columns .column {flex-basis: 100%;margin: 0 0 5px 0;}}body {font-size:80%;color:#222;background:#fff;} 
